query {
	allFilms {
    films {
      id
      episodeID
      title
      director
    }
  }
}

query($id: ID!) { 
	film(filmID: $id) {
    episodeID
    title
    director
    releaseDate
  }
}

query($id: ID!) { 
	planet(planetID: $id) {
    id
    name
    diameter
    rotationPeriod
    orbitalPeriod
    gravity
    population
  }
}
